# combining the files using sqlite3

db = "usa_mass.db"

def run_query(q):
    with sqlite3.connect(db) as conn:
            return pd.read_sql(q,conn)

def run_command(c):
    with sqlite3.connect(db) as conn:
        conn.execute('PRAGMA foreign_keys = ON;')        
        conn.isolation_level = None
        conn.execute(c)

def show_tables():
    q = '''
        SELECT 
              name,
              type
        FROM sqlite_master
        WHERE type IN ("table", "view");
        '''
    return run_query(q)

